var isSubStructure = function(A, B) {
  if (!B) return false;

  let isSub = false;
  preOrder(A);

  function preOrder(node) {
    if (!node) return '';
    if (node.val === B.val) {
      if (isSubOf(node, B)) {
        isSub = true;
        return;
      }
    }
    preOrder(node.left);
    preOrder(node.right);
  }

  function isSubOf(sup, sub) {
    if (!sub) return true;
    if (!sup || sup.val !== sub.val) {
      return false;
    }
    return isSubOf(sup.left, sub.left) && isSubOf(sup.right, sub.right);
  }

  return isSub;
};
```

